parameters.jsCode:
// Parse Claude response
const claudeResponse = JSON.parse($input.first().json.content[0].text);
const params = $('Set Article Params').first().json;

// Generate slug from title
const slug = claudeResponse.title
  .toLowerCase()
  .normalize('NFD')
  .replace(/[\u0300-\u036f]/g, '')
  .replace(/[^a-z0-9]+/g, '-')
  .replace(/^-|-$/g, '');

// Format hashtags for social media
const hashtags = ['#CinematicAds', '#AIVideo', '#VideoMarketing', ...claudeResponse.hashtags];
const hashtagsString = hashtags
  .map(h => h.startsWith('#') ? h : '#' + h)
  .slice(0, 8)
  .join(' ');

// Build article URL
const articleUrl = `https://${params.brandDomain}/blog/${slug}`;

return {
  // Article data
  title: claudeResponse.title,
  slug: slug,
  excerpt: claudeResponse.excerpt,
  content: claudeResponse.content,
  hashtags: hashtags,
  hashtagsString: hashtagsString,

  // Meta
  language: params.language,
  publishedDate: new Date().toISOString().split('T')[0],
  brandName: params.brandName,
  brandDomain: params.brandDomain,
  articleUrl: articleUrl,

  // Social content - CinematicAds branded
  facebookPost: `ðŸŽ¬ ${claudeResponse.excerpt}\n\n${hashtagsString}\n\nðŸ“– Read more: ${articleUrl}\n\nðŸš€ Create stunning video ads in minutes: ${params.ctaUrl}`,
  linkedInPost: `${claudeResponse.excerpt}\n\nAt CinematicAds, we're revolutionizing how brands create video content with AI.\n\n${hashtagsString}\n\nðŸ“– Full article: ${articleUrl}`,

  // Publishing flags
  publishToFacebook: params.publishToFacebook,
  publishToLinkedIn: params.publishToLinkedIn
};

// Aggregate results from all branches
const article = $('Parse & Format Content').first().json;
const fileResult = $('Save HTML File').first()?.json || null;
const fbResult = $('Post to Facebook Page').first()?.json || null;
const liResult = $('Post to LinkedIn').first()?.json || null;

return {
  success: true,
  brand: 'CinematicAds',
  article: {
    title: article.title,
    slug: article.slug,
    url: article.articleUrl,
    publishedDate: article.publishedDate
  },
  distribution: {
    website: {
      status: 'generated',
      path: `/output/cinematicads/blog/${article.slug}.html`
    },
    facebook: fbResult ? {
      status: 'published',
      postId: fbResult.id || 'success'
    } : {
      status: 'skipped',
      reason: article.publishToFacebook ? 'Error or not configured' : 'Not enabled'
    },
    linkedin: liResult ? {
      status: 'published',
      postId: liResult.id || 'success'
    } : {
      status: 'skipped',
      reason: article.publishToLinkedIn ? 'Error or not configured' : 'Not enabled'
    }
  }
};
